--
-- Структура таблицы `services`
--

CREATE TABLE IF NOT EXISTS `services` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `h1` varchar(100) DEFAULT NULL,
  `url` varchar(100) NOT NULL,
  `md5_url` char(32) NOT NULL,
  `main_image` varchar(255) DEFAULT NULL,
  `more_images` text NOT NULL,
  `s_title` varchar(255) DEFAULT NULL,
  `s_description` varchar(350) DEFAULT NULL,
  `s_keywords` varchar(255) DEFAULT NULL,
  `create_date` timestamp NULL DEFAULT NULL,
  `update_date` timestamp NULL DEFAULT NULL,
  `content` text,
  `short_content` text,
  `active` tinyint(1) NOT NULL DEFAULT '1',
  `section_id` int(10) unsigned NOT NULL,
  PRIMARY KEY (`id`,`active`),
  UNIQUE KEY `md5_url` (`md5_url`,`active`),
  KEY `section` (`section_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Структура таблицы `services_sections`
--

CREATE TABLE IF NOT EXISTS `services_sections` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(50) NOT NULL,
  `url` varchar(100) NOT NULL,
  `md5_url` char(32) NOT NULL,
  `active` tinyint(1) NOT NULL DEFAULT '1',
  `s_title` varchar(255) DEFAULT NULL,
  `s_description` varchar(350) DEFAULT NULL,
  `s_keywords` varchar(255) DEFAULT NULL,
  `description` text,
  `position` int(10) DEFAULT NULL,
  `h1` varchar(255) DEFAULT NULL,
  `head_filter` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`,`active`),
  UNIQUE KEY `md5_url` (`md5_url`,`active`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- Структура таблицы `users`
--

CREATE TABLE IF NOT EXISTS `users` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `email` varchar(127) NOT NULL,
  `password` char(64) DEFAULT NULL,
  `roles` int(10) unsigned NOT NULL DEFAULT '0',
  `name` varchar(255) DEFAULT NULL,
  `logins` int(10) unsigned NOT NULL DEFAULT '0',
  `last_login` int(10) unsigned DEFAULT NULL,
  `login_attempts` int(10) unsigned DEFAULT NULL,
  `banned_to` int(10) unsigned DEFAULT NULL,
  `ban_time` int(10) unsigned DEFAULT NULL,
  `reset_password_code` varchar(32) DEFAULT NULL,
  `created_at` int(10) unsigned NOT NULL,
  `updated_at` int(10) unsigned DEFAULT NULL,
  `register_token` char(32) DEFAULT NULL,
  `active` tinyint(1) NOT NULL DEFAULT '0',
  `username` char(32) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `uniq_email` (`email`),
  KEY `register_token` (`email`,`register_token`),
  KEY `username` (`username`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

